CREATE OR REPLACE PACKAGE student_utils AS PROCEDURE add_student(p_id NUMBER, p_fname VARCHAR2, p_lname VARCHAR2); FUNCTION get_student_by_id(p_id NUMBER) RETURN A_STUDENTS%ROWTYPE; END student_utils; 
/
CREATE OR REPLACE PACKAGE BODY student_utils AS PROCEDURE add_student(p_id NUMBER, p_fname VARCHAR2, p_lname VARCHAR2) IS BEGIN INSERT INTO A_STUDENTS (STUDENTID, FIRSTNAME, LASTNAME) VALUES (p_id, p_fname, p_lname); END; FUNCTION get_student_by_id(p_id NUMBER) RETURN A_STUDENTS%ROWTYPE IS v_student A_STUDENTS%ROWTYPE; BEGIN SELECT * INTO v_student FROM A_STUDENTS WHERE STUDENTID = p_id; RETURN v_student; END; END student_utils;

